import React , {useState, useEffect } from 'react';
import axios from './axios';
import requests from './requests';
import './Banner.css';

function Banner() {

    const [movie, setMovie] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const request = await axios.get(requests.fetchNetflixOriginals);
            setMovie(
            request.data.results[
                Math.floor(Math.random() * request.data.results.length)
            ]);
            return request;
        }
        fetchData();
    }, [])

    // console.log(movie);
    function truncate(str, n){
        return str?.length > n ? str.substr(0, n - 1 ) + "..." : str;
    }

    return (
        <header className="banner"
            style={{backgroundSize: "cover", backgroundImage: 
            `url("https://image.tmdb.org/t/p/original/${movie?.backdrop_path}")`, 
            backgroundPosition: "center center",
        }}>

            <div className="banner__contents">
                {/* title */}
                <h1 className="banner__title">
                    {movie?.title || movie?.name || movie?.original_name}
                </h1>
                {/* div> 2 buttons */}
                <h1 className="banner__description">
                    {truncate(movie?.overview, 150)}
                </h1>
                <div className="banner__buttons">

                    <button className="banner__button_play">
                        {/* <div> */}
                            <svg className="banner__button_icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M6 4l15 8-15 8z" fill="currentColor"/></svg>
                        {/* </div> */}
                        {/* <div> */}
                            Assistir
                            {/* </div> */}
                    </button>
                    <button className="banner__button">Mais informações</button>
                </div>

            </div>

            <div className="banner__fadeBottom"></div>

        </header>
    )
}

export default Banner
